import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import clsx from 'clsx';

import Episodes from './Episodes';
import Loading from '../assets/dual-ring-1s-200px.svg';

const Wrapper = styled.div`
  &.show {
    display: flex;
    justify-content: center;
    align-items: center;
  }
  display: none;

  position: fixed;
  top: 0;
  left: 0;
  background-color: #00000064;
  width: 100%;
  height: 100%;
  z-index: 5000;

  .podcast-content {
    background: var(--white);
    padding: 1rem;
    border-radius: 5px;
    overflow: auto;

    width: 80vw;
    max-width: 100%;
    height: 80vh;
    max-height: 100%;

    position: fixed;
    z-index: 100;

    display: grid;
    grid-template-columns: 2fr 1fr;
    gap: 2rem;
  }
  .podcast-info {
    .podcast-host {
      margin: 0 0 0.5rem 0;
      font-weight: 300;
      font-size: 1.2rem;
      a {
        text-decoration: none;
        color: var(--primary);
      }
    }
    .podcast-title {
      color: var(--dark);
      font-weight: 700;
      font-size: 3rem;
      margin: 0 0 1rem 0;
    }
    .podcast-description {
      line-height: 1.5;
    }
  }
  .podcast-cover {
    img {
      width: 100%;
    }
  }

  .clear-icon {
    width: 25px;
    position: absolute;
    top: 10px;
    right: 10px;
  }
`;

const Overlay = ({ isVisible, setVisible, overlayData, setOverlayData }) => {
  const [isLoading, setLoading] = useState(false);

  const { host, title, image, episodes } = overlayData;

  // Remove HTML Tags
  let { description } = overlayData;
  if (description !== undefined) {
    description = description.replace(/(<([^>]+)>)/gi, '');
  }

  window.onscroll = () => {
    if (
      window.innerHeight + document.documentElement.scrollTop ===
      document.documentElement.offsetHeight
    ) {
      console.log("it's the overlay end");
    }
  };

  // Set loading image
  useEffect(() => {
    if (Object.keys(overlayData).length === 0) {
      setLoading(true);
    } else {
      setLoading(false);
    }
  }, [isVisible, overlayData]);

  return (
    <Wrapper
      className={clsx('podcast-overlay', { show: isVisible })}
      onClick={(e) => {
        if (e.target.classList.contains('show')) {
          setVisible(false);
          setOverlayData({});
        }
      }}
    >
      {isLoading ? (
        <img src={Loading} alt="loading" />
      ) : (
        <>
          <div className="podcast-content">
            <div className="podcast-info">
              <div className="podcast-host">{host}</div>
              <div className="podcast-title">{title}</div>
              <div className="podcast-description">{description}</div>
            </div>
            <div className="podcast-cover">
              <img src={image} alt="" />
            </div>
            {/*  */}

            {isVisible ? <Episodes episodes={episodes} /> : ''}
          </div>
          {/*  */}
        </>
      )}
      <img
        className="clear-icon"
        alt="clear-icon"
        src="data:image/svg+xml;utf8;base64,"
        onClick={(e) => {
          setVisible(false);
          setOverlayData({});
        }}
      />
    </Wrapper>
  );
};

export default Overlay;
